    this.state = {
      numero:0
    };

    this.timer = null;
    this.vai = this.vai.bind(this);
    this.limpar = this.limpar.bind(this);
  }

  vai(){
    this.timer = setInterval(()=>{
      let state = this.state;
      state.numero += 0.1;
      this.setState(state);
    },100);
  }

